import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { LogIn, CreditCard, Sparkles, User } from 'lucide-react';
import axios from 'axios';

const Navbar = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem('user_id');
  const [status, setStatus] = useState({ remaining: 0, role: '' });

  // Kullanıcının güncel hak bilgisini backend'den çekiyoruz
  useEffect(() => {
    if (userId) {
      const fetchStatus = async () => {
        try {
          const res = await axios.get(`http://localhost:8000/api/user-status/${userId}`);
          setStatus({ remaining: res.data.remaining, role: res.data.role });
        } catch (e) { console.error(e); }
      };
      fetchStatus();
    }
  }, [userId]);

  return (
    <div className="fixed top-0 right-0 left-0 h-20 z-40 flex items-center justify-end px-12 pointer-events-none">
      <div className="flex items-center gap-3 pointer-events-auto bg-white/90 backdrop-blur-md p-2 rounded-2xl border border-white/20 shadow-xl">
        
        {/* Giriş Yapılmamışsa Gösterilecek Butonlar */}
        {!userId ? (
          <>
            <button onClick={() => navigate('/pricing')} className="flex items-center gap-2 px-5 py-2.5 rounded-xl font-bold text-gray-600 hover:bg-gray-50 transition-all">
              <CreditCard size={18} /> <span>Paketler</span>
            </button>
            <button onClick={() => navigate('/auth')} className="flex items-center gap-2 px-6 py-2.5 bg-black text-white rounded-xl font-bold hover:scale-105 transition-all">
              <LogIn size={18} /> <span>Giriş Yap</span>
            </button>
          </>
        ) : (
          /* Giriş Yapılmışsa Gösterilecek Hak Paneli */
          <div className="flex items-center gap-4 px-4 py-1">
            <div className="flex flex-col items-end">
              <span className="text-[10px] font-black text-[#10B981] uppercase tracking-tighter">
                {status.role === 'member' ? 'Üye (Hediye Hak)' : 'Pro Üye'}
              </span>
              <span className="text-xs font-bold text-gray-400">
                Kalan Hak: <span className="text-black">{status.remaining}</span>
              </span>
            </div>
            <button 
              onClick={() => navigate('/profile')}
              className="w-10 h-10 rounded-xl bg-gray-100 flex items-center justify-center text-gray-600 hover:bg-[#10B981] hover:text-white transition-all shadow-inner"
            >
              <User size={20} />
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Navbar;
