import React, { useState } from 'react';
import { Mail, Lock, LogIn, UserPlus, User as UserIcon, Calendar } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [formData, setFormData] = useState({ firstName: '', lastName: '', age: '' });
  const navigate = useNavigate();

  const handleAuth = async (e) => {
    e.preventDefault();
    try {
      // Backend'deki /login endpoint'ine istek atıyoruz
      const response = await axios.post('http://localhost:8000/api/login', { email });

      if (response.data.status === "success") {
        localStorage.setItem('user_id', response.data.user_id);
        localStorage.setItem('user_role', response.data.role);
        // Giriş sonrası isim bilgisi de saklanabilir
        if (!isLogin) localStorage.setItem('user_name', formData.firstName);
        
        navigate('/'); // Ana sayfaya yönlendirme
      }
    } catch (error) {
      alert("Sunucuya bağlanılamadı! Lütfen Python sunucusunu kontrol edin.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#F9FAFB] p-6">
      <div className="w-full max-w-md bg-white rounded-[3rem] shadow-2xl p-10 border border-gray-100">
        <div className="text-center mb-10">
          <h2 className="text-3xl font-black text-gray-900 mb-2">
            {isLogin ? 'Tekrar Hoş Geldin' : 'Aramıza Katıl'}
          </h2>
          <p className="text-gray-500 font-medium">
            {isLogin ? 'Analizlerine devam et.' : 'Ücretsiz analiz hakkını 7\'ye çıkar!'}
          </p>
        </div>

        <form className="space-y-4" onSubmit={handleAuth}>
          {!isLogin && (
            <>
              <div className="grid grid-cols-2 gap-4">
                <div className="relative">
                  <UserIcon className="absolute left-4 top-4 text-gray-400" size={18} />
                  <input 
                    type="text" placeholder="Ad" required
                    onChange={(e) => setFormData({...formData, firstName: e.target.value})}
                    className="w-full h-14 pl-11 pr-4 rounded-2xl bg-gray-50 outline-none border-2 border-transparent focus:border-[#10B981]/30 transition-all text-sm" 
                  />
                </div>
                <input 
                  type="text" placeholder="Soyad" required
                  onChange={(e) => setFormData({...formData, lastName: e.target.value})}
                  className="w-full h-14 px-6 rounded-2xl bg-gray-50 outline-none border-2 border-transparent focus:border-[#10B981]/30 transition-all text-sm" 
                />
              </div>
              <div className="relative">
                <Calendar className="absolute left-4 top-4 text-gray-400" size={18} />
                <input 
                  type="number" placeholder="Yaş" required
                  onChange={(e) => setFormData({...formData, age: e.target.value})}
                  className="w-full h-14 pl-11 pr-4 rounded-2xl bg-gray-50 outline-none border-2 border-transparent focus:border-[#10B981]/30 transition-all text-sm" 
                />
              </div>
            </>
          )}

          <div className="relative">
            <Mail className="absolute left-4 top-4 text-gray-400" size={20} />
            <input 
              type="email" required placeholder="E-posta"
              value={email} onChange={(e) => setEmail(e.target.value)}
              className="w-full h-14 pl-12 pr-6 rounded-2xl bg-gray-50 outline-none border-2 border-transparent focus:border-[#10B981]/30 transition-all" 
            />
          </div>
          <div className="relative">
            <Lock className="absolute left-4 top-4 text-gray-400" size={20} />
            <input 
              type="password" required placeholder="Şifre"
              value={password} onChange={(e) => setPassword(e.target.value)}
              className="w-full h-14 pl-12 pr-6 rounded-2xl bg-gray-50 outline-none border-2 border-transparent focus:border-[#10B981]/30 transition-all" 
            />
          </div>
          
          <button type="submit" className="w-full h-14 bg-black text-white rounded-2xl font-bold flex items-center justify-center gap-2 hover:scale-[1.02] active:scale-95 transition-all mt-6 shadow-lg shadow-black/10">
            {isLogin ? <LogIn size={20} /> : <UserPlus size={20} />}
            {isLogin ? 'Giriş Yap' : 'Kayıt Ol'}
          </button>
        </form>

        <div className="mt-8 text-center">
          <button onClick={() => setIsLogin(!isLogin)} className="text-sm font-bold text-gray-400 hover:text-[#10B981] transition-all">
            {isLogin ? 'Hesabın yok mu? Kayıt Ol' : 'Zaten üye misin? Giriş Yap'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
