// ... üst kısımdaki importlar aynı ...
const [mode, setMode] = useState('eco'); // Varsayılan mod: Doğa Dostu

// handleSearch fonksiyonunda isteği güncelle:
const handleSearch = async () => {
  if (!brand.trim()) return;
  setLoading(true);
  try {
    const response = await axios.post('http://localhost:8000/api/analyze', { 
      brand_name: brand, 
      mode: mode // Modu backend'e gönderiyoruz
    });
    setResult(response.data);
  } catch (e) { console.error(e); } finally { setLoading(false); }
};

// Return kısmına (Arama çubuğunun üstüne) şu butonları ekle:
<div className="flex gap-4 mb-8">
  <button 
    onClick={() => setMode('eco')}
    className={`px-6 py-2 rounded-full font-medium transition-all ${mode === 'eco' ? 'bg-primary text-white' : 'bg-gray-100 text-gray-500'}`}
  >
    Doğa Dostu mu?
  </button>
  <button 
    onClick={() => setMode('safety')}
    className={`px-6 py-2 rounded-full font-medium transition-all ${mode === 'safety' ? 'bg-secondary text-white' : 'bg-gray-100 text-gray-500'}`}
  >
    Güvenli Alışveriş mi?
  </button>
</div>
