import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { User, Activity } from 'lucide-react';
import axios from 'axios';

const Navbar = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem('user_id');
  const userName = localStorage.getItem('user_name') || 'Kullanıcı';
  const [status, setStatus] = useState({ remaining: 3 });

  useEffect(() => {
    if (userId) {
      const fetchStatus = async () => {
        try {
          const res = await axios.get(`http://localhost:8000/api/user-status/${userId}`);
          setStatus({ remaining: res.data.remaining });
        } catch (e) { console.error("Veri çekilemedi", e); }
      };
      fetchStatus();
    }
  }, [userId]);

  return (
    <div className="fixed top-0 right-0 left-0 h-20 z-40 flex items-center justify-end px-12 pointer-events-none">
      <div className="flex items-center gap-4 pointer-events-auto bg-white/40 backdrop-blur-md p-2 px-4 rounded-2xl border border-gray-100/50 shadow-sm transition-all hover:bg-white/80">
        
        {/* Giriş Yapılmamışsa */}
        {!userId ? (
          <button 
            onClick={() => navigate('/auth')} 
            className="text-sm font-bold text-gray-600 hover:text-black transition-all px-4"
          >
            Giriş Yap
          </button>
        ) : (
          /* Sade Kullanıcı Paneli: Sadece İsim ve Hak */
          <div className="flex items-center gap-6">
            <div className="flex flex-col items-end">
              <span className="text-sm font-bold text-gray-900 font-manrope lowercase opacity-80">
                @{userName.replace(/\s+/g, '').toLowerCase()}
              </span>
              <div className="flex items-center gap-1.5">
                <div className="w-1.5 h-1.5 rounded-full bg-[#10B981] animate-pulse" />
                <span className="text-[10px] font-bold text-gray-400 uppercase tracking-widest">
                  {status.remaining} ANALİZ HAKKI
                </span>
              </div>
            </div>

            <div 
              onClick={() => navigate('/profile')}
              className="w-10 h-10 rounded-full bg-gray-50 flex items-center justify-center text-gray-400 cursor-pointer hover:bg-black hover:text-white transition-all border border-gray-100"
            >
              <User size={18} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Navbar;
