import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import Sidebar from './components/Sidebar';
import AnalysisPage from './pages/AnalysisPage';
import HistoryPage from './pages/HistoryPage';
import ProfilePage from './pages/ProfilePage'; // Profil sayfamızı da ekledik
import './index.css';

function App() {
  // Sidebar'ın açık/kapalı durumunu yönetiyoruz
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  return (
    <AuthProvider>
      <Router>
        <div className="flex bg-[#F9FAFB] min-h-screen transition-all duration-300">
          
          {/* Sol Menü: Durum bilgilerini gönderiyoruz */}
          <Sidebar isOpen={isSidebarOpen} setIsOpen={setIsSidebarOpen} />
          
          {/* Ana İçerik Alanı: Sidebar kapalıyken ml-20 (dar), açıkken ml-[280px] (geniş) */}
          <main className={`flex-1 transition-all duration-300 ${isSidebarOpen ? 'ml-[280px]' : 'ml-20'}`}>
            <Routes>
              {/* 1. Sayfa: Akıllı Analiz Ekranı */}
              <Route path="/" element={<AnalysisPage />} />
              
              {/* 2. Sayfa: Analizlerin Listelendiği Geçmiş */}
              <Route path="/history" element={<HistoryPage />} />
              
              {/* 3. Sayfa: İstatistiklerin Olduğu Profil Ekranı */}
              <Route path="/profile" element={<ProfilePage />} />
              
              {/* Not: Gelecekte eklenecek diğer sayfalar buraya Route olarak eklenebilir */}
            </Routes>
          </main>
          
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
