import React, { useState } from 'react';
import { Search, Sparkles, Leaf, ShieldCheck, AlertCircle, CheckCircle2 } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const AnalysisPage = () => {
  const { token, loginAsGuest } = useAuth();
  const [brand, setBrand] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [mode, setMode] = useState('eco'); // 'eco' veya 'safety'

  const handleSearch = async () => {
    if (!brand.trim()) return;
    setLoading(true);
    setResult(null); // Yeni arama için eskiyi temizle
    try {
      if (!token) await loginAsGuest();
      // Backend'e hem marka adını hem de seçilen modu gönderiyoruz
      const response = await axios.post('http://localhost:8000/api/analyze', { 
        brand_name: brand,
        mode: mode 
      });
      setResult(response.data);
    } catch (e) { 
      console.error(e); 
    } finally { 
      setLoading(false); 
    }
  };

  return (
    <div className="p-12 lg:p-20 flex flex-col items-center min-h-screen bg-[#F9FAFB]">
      {/* Başlık Bölümü */}
      <div className="text-center mb-12">
        <h1 className="text-5xl lg:text-6xl font-black mb-4 bg-gradient-to-r from-[#10B981] to-[#3B82F6] bg-clip-text text-transparent font-manrope tracking-tight">
          Akıllı Marka Analizi
        </h1>
        <p className="text-gray-500 text-lg font-medium">Yapay zeka ile sürdürülebilirlik ve güvenlik denetimi</p>
      </div>

      {/* Kategori Seçici (Modlar) */}
      <div className="flex gap-3 mb-10 p-1.5 bg-gray-200/50 rounded-2xl">
        <button 
          onClick={() => setMode('eco')}
          className={`flex items-center gap-2 px-6 py-3 rounded-xl font-bold transition-all duration-300 ${
            mode === 'eco' ? 'bg-white text-[#10B981] shadow-md scale-105' : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          <Leaf size={20} /> Doğa Dostu
        </button>
        <button 
          onClick={() => setMode('safety')}
          className={`flex items-center gap-2 px-6 py-3 rounded-xl font-bold transition-all duration-300 ${
            mode === 'safety' ? 'bg-white text-[#3B82F6] shadow-md scale-105' : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          <ShieldCheck size={20} /> Güvenli Alışveriş
        </button>
      </div>

      {/* Arama Çubuğu */}
      <div className="relative w-full max-w-2xl group shadow-2xl rounded-full">
        <input 
          className="w-full h-20 px-10 rounded-full bg-white outline-none text-xl border-2 border-transparent focus:border-[#10B981]/30 transition-all placeholder:text-gray-300 shadow-sm" 
          placeholder={mode === 'eco' ? "Hangi markanın doğasını inceleyelim?" : "Hangi mağazanın güvenliğini sorgulayalım?"}
          value={brand} 
          onChange={(e) => setBrand(e.target.value)} 
          onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
        />
        <button 
          onClick={handleSearch} 
          disabled={loading}
          className={`absolute right-3 top-3 h-14 px-8 rounded-full flex items-center gap-2 font-bold transition-all ${
            loading ? 'bg-gray-400' : 'bg-black text-white hover:scale-[1.02] active:scale-95'
          }`}
        >
          {loading ? <Sparkles className="animate-spin" /> : <Search size={22} />}
          <span>{loading ? 'Analiz Ediliyor...' : 'Analiz Et'}</span>
        </button>
      </div>

      {/* Profesyonel Sonuç Kartı */}
      {result && (
        <div className="mt-12 w-full max-w-2xl animate-in fade-in slide-in-from-bottom-4 duration-500">
          <div className="p-10 border border-gray-100 rounded-[3rem] bg-white shadow-[0_32px_64px_-15px_rgba(0,0,0,0.1)] relative overflow-hidden">
            
            {/* Arkaplan Süsleme */}
            <div className={`absolute top-0 right-0 w-32 h-32 opacity-5 -mr-8 -mt-8 rounded-full ${mode === 'eco' ? 'bg-[#10B981]' : 'bg-[#3B82F6]'}`} />

            <div className="flex justify-between items-start mb-8">
              <div>
                <span className={`text-xs font-bold uppercase tracking-widest px-3 py-1 rounded-full ${mode === 'eco' ? 'bg-[#10B981]/10 text-[#10B981]' : 'bg-[#3B82F6]/10 text-[#3B82F6]'}`}>
                  {mode === 'eco' ? 'Ekolojik Rapor' : 'Güvenlik Raporu'}
                </span>
                <h2 className="text-4xl font-black mt-3 text-gray-900">{result.brand_name}</h2>
              </div>
              <div className="text-right">
                <div className={`text-5xl font-black ${result.puan > 80 ? 'text-[#10B981]' : 'text-orange-500'}`}>
                  {result.puan}<span className="text-lg text-gray-400">/100</span>
                </div>
                <p className="text-sm font-bold text-gray-400 mt-1 uppercase tracking-tighter">Analiz Skoru</p>
              </div>
            </div>

            <div className="flex items-start gap-4 p-6 bg-gray-50 rounded-3xl border border-gray-100 mb-6">
              <div className={`p-3 rounded-2xl ${mode === 'eco' ? 'bg-[#10B981]/10' : 'bg-[#3B82F6]/10'}`}>
                {result.puan > 80 ? <CheckCircle2 className="text-[#10B981]" size={28} /> : <AlertCircle className="text-orange-500" size={28} />}
              </div>
              <div>
                <h4 className="font-bold text-gray-900 mb-1">Yapay Zeka Tespiti</h4>
                <p className="text-gray-600 leading-relaxed font-medium">
                  {result.tespit}
                </p>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="p-4 bg-gray-50 rounded-2xl text-center">
                <p className="text-xs font-bold text-gray-400 uppercase mb-1">Risk Durumu</p>
                <p className="font-bold text-gray-900">{result.risk || 'Düşük'}</p>
              </div>
              <div className="p-4 bg-gray-50 rounded-2xl text-center">
                <p className="text-xs font-bold text-gray-400 uppercase mb-1">Veri Kaynağı</p>
                <p className="font-bold text-gray-900">GreenScan AI v2</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AnalysisPage;
