import React from 'react';
import { useNavigate } from 'react-router-dom';
import { LogIn, CreditCard } from 'lucide-react';

const Navbar = () => {
  const navigate = useNavigate();
  // Yerel depolamadan kullanıcı ID'sini kontrol ediyoruz
  const isLoggedIn = localStorage.getItem('user_id');

  // Eğer kullanıcı giriş yapmışsa Navbar hiçbir şey döndürmez (boş kalır)
  if (isLoggedIn) return null;

  return (
    <div className="fixed top-0 right-0 left-0 h-20 z-40 flex items-center justify-end px-12 pointer-events-none">
      <div className="flex items-center gap-4 pointer-events-auto bg-white/80 backdrop-blur-md p-2 rounded-2xl border border-white/20 shadow-lg">
        <button 
          onClick={() => navigate('/pricing')}
          className="flex items-center gap-2 px-5 py-2.5 rounded-xl font-bold text-gray-600 hover:bg-gray-50 transition-all border border-transparent hover:border-gray-100"
        >
          <CreditCard size={18} />
          <span>Paketler</span>
        </button>

        <button 
          onClick={() => navigate('/auth')}
          className="flex items-center gap-2 px-6 py-2.5 rounded-xl bg-black text-white font-bold hover:scale-[1.05] active:scale-95 transition-all shadow-xl shadow-black/10"
        >
          <LogIn size={18} />
          <span>Giriş Yap</span>
        </button>
      </div>
    </div>
  );
};

export default Navbar;
