import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { LogIn, CreditCard, User, Zap } from 'lucide-react'; // Zap simgesi premium için ideal
import axios from 'axios';

const Navbar = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem('user_id');
  const userName = localStorage.getItem('user_name') || 'Kullanıcı';
  const [status, setStatus] = useState({ remaining: 3, role: 'guest' });

  useEffect(() => {
    if (userId) {
      const fetchStatus = async () => {
        try {
          const res = await axios.get(`http://localhost:8000/api/user-status/${userId}`);
          setStatus({ remaining: res.data.remaining, role: res.data.role });
        } catch (e) { console.error(e); }
      };
      fetchStatus();
    }
  }, [userId]);

  return (
    <div className="fixed top-0 right-0 left-0 h-20 z-40 flex items-center justify-end px-12 pointer-events-none">
      <div className="flex items-center gap-4 pointer-events-auto bg-white/90 backdrop-blur-md p-2.5 rounded-2xl border border-white/20 shadow-xl shadow-black/5">
        
        {/* PREMIUM LOGO / BUTONU: Her zaman görünür ve dikkat çekici */}
        <button 
          onClick={() => navigate('/pricing')}
          className="flex items-center gap-2 px-4 py-2 rounded-xl bg-gradient-to-r from-amber-400 to-orange-500 text-white font-black text-[10px] tracking-widest uppercase shadow-lg shadow-orange-200 hover:scale-105 transition-all group"
        >
          <Zap size={14} fill="currentColor" className="group-hover:animate-pulse" />
          <span>PRO'YA GEÇ</span>
        </button>

        <div className="w-[1px] h-8 bg-gray-100 mx-1" /> {/* Ayırıcı Çizgi */}

        {!userId ? (
          <button 
            onClick={() => navigate('/auth')} 
            className="px-6 py-2 bg-black text-white rounded-xl font-bold text-sm hover:bg-gray-800 transition-all"
          >
            Giriş Yap
          </button>
        ) : (
          <div className="flex items-center gap-4 px-2">
            <div className="flex flex-col items-end">
              <span className="text-sm font-black text-gray-900 font-manrope">{userName}</span>
              <span className="text-[9px] font-bold text-[#10B981] uppercase tracking-tighter">
                Kalan Hak: {status.remaining}
              </span>
            </div>
            <div 
              onClick={() => navigate('/profile')}
              className="w-10 h-10 rounded-xl bg-gray-50 flex items-center justify-center text-gray-500 cursor-pointer hover:bg-black hover:text-white transition-all border border-gray-100 shadow-sm"
            >
              <User size={20} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Navbar;
