import React, { useState, useEffect } from 'react';
import { Search, Sparkles, Leaf, ShieldCheck, AlertCircle, CheckCircle2, Download, Lock } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

const AnalysisPage = () => {
  const { token, loginAsGuest } = useAuth();
  const [brand, setBrand] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [mode, setMode] = useState('eco');
  const [userStats, setUserStats] = useState({ remaining: 3, role: 'guest' });

  const isLoggedIn = localStorage.getItem('user_id');
  const firstName = localStorage.getItem('user_name') || '';

  useEffect(() => {
    const fetchStatus = async () => {
      const userId = localStorage.getItem('user_id') || 'guest_user';
      try {
        const res = await axios.get(`http://localhost:8000/api/user-status/${userId}`);
        setUserStats({ remaining: res.data.remaining, role: res.data.role });
      } catch (e) { 
        console.error("Statü bilgisi güncellenemedi", e); 
      }
    };
    fetchStatus();
  }, [result]); 

  const downloadPDF = async () => {
    const element = document.getElementById('report-card');
    const canvas = await html2canvas(element, { scale: 2 });
    const imgData = canvas.toDataURL('image/png');
    const pdf = new jsPDF('p', 'mm', 'a4');
    const imgProps = pdf.getImageProperties(imgData);
    const pdfWidth = pdf.internal.pageSize.getWidth();
    const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
    
    pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
    pdf.save(`GreenScan_Analiz_${result.brand_name}.pdf`);
  };

  const handleSearch = async () => {
    if (!brand.trim()) return;
    setLoading(true);
    setResult(null);
    const currentUserId = localStorage.getItem('user_id') || 'guest_user';

    try {
      if (!token) await loginAsGuest();
      const response = await axios.post('http://localhost:8000/api/analyze', { 
        brand_name: brand,
        mode: mode,
        user_id: currentUserId 
      });

      if (response.data.error) {
        alert(response.data.error + "\n" + response.data.message);
        return;
      }
      setResult(response.data);
    } catch (e) { 
      console.error(e); 
      alert("Analiz sırasında bir hata oluştu.");
    } finally { 
      setLoading(false); 
    }
  };

  return (
    <div className="p-8 lg:p-12 flex flex-col items-center min-h-screen bg-[#F9FAFB]">
      
      {/* BAŞLIK: Boyutlar text-3xl ve text-4xl seviyesine çekilerek kibarlaştırıldı */}
      <div className="text-center mb-10 mt-6 animate-in fade-in slide-in-from-top-4 duration-700 w-full max-w-2xl">
        <h1 className="text-3xl lg:text-4xl font-black mb-3 bg-gradient-to-r from-[#10B981] to-[#3B82F6] bg-clip-text text-transparent font-manrope leading-normal tracking-normal">
          {isLoggedIn 
            ? `Merhaba ${firstName}, bugün neyi inceleyelim?` 
            : "Akıllı Marka Analizi"}
        </h1>
        <p className="text-gray-400 text-sm font-medium italic opacity-75">
          Yapay zeka ile sürdürülebilirlik ve güvenlik denetimi parmaklarınızın ucunda.
        </p>
      </div>

      {/* MOD SEÇİCİ: Daha küçük ve nezih butonlar */}
      <div className="flex gap-2 mb-8 p-1 bg-gray-200/50 rounded-xl">
        <button 
          onClick={() => setMode('eco')} 
          className={`flex items-center gap-2 px-5 py-2 rounded-lg text-xs font-bold transition-all ${
            mode === 'eco' ? 'bg-white text-[#10B981] shadow-sm' : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          <Leaf size={14} /> Doğa Dostu
        </button>
        <button 
          onClick={() => setMode('safety')} 
          className={`flex items-center gap-2 px-5 py-2 rounded-lg text-xs font-bold transition-all ${
            mode === 'safety' ? 'bg-white text-[#3B82F6] shadow-sm' : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          <ShieldCheck size={14} /> Güvenli Alışveriş
        </button>
      </div>

      {/* ARAMA ÇUBUĞU: Yükseklik h-16 yapılarak estetik kazandırıldı */}
      <div className="relative w-full max-w-xl group shadow-sm rounded-full">
        <input 
          className="w-full h-16 px-8 rounded-full bg-white outline-none text-base border-2 border-transparent focus:border-[#10B981]/10 transition-all placeholder:text-gray-300" 
          placeholder={mode === 'eco' ? "Marka adı girin..." : "Mağaza adı girin..."}
          value={brand} 
          onChange={(e) => setBrand(e.target.value)} 
          onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
        />
        <button 
          onClick={handleSearch} 
          disabled={loading} 
          className={`absolute right-2 top-2 h-12 px-6 rounded-full flex items-center gap-2 font-bold transition-all ${
            loading ? 'bg-gray-400' : 'bg-black text-white active:scale-95 shadow-md'
          }`}
        >
          {loading ? <Sparkles size={16} className="animate-spin" /> : <Search size={18} />}
          <span className="text-xs">{loading ? 'İşleniyor...' : 'Analiz Et'}</span>
        </button>
      </div>

      {/* SONUÇ KARTI: İçeriklerin tam sığması için padding ve fontlar optimize edildi */}
      {result && (
        <div className="mt-10 w-full max-w-xl animate-in fade-in slide-in-from-bottom-6 duration-500">
          <div id="report-card" className="p-8 border border-gray-100 rounded-[2.5rem] bg-white shadow-xl relative overflow-hidden">
            <div className={`absolute top-0 right-0 w-32 h-32 opacity-5 -mr-8 -mt-8 rounded-full ${mode === 'eco' ? 'bg-[#10B981]' : 'bg-[#3B82F6]'}`} />
            
            <div className="flex justify-between items-start mb-6 relative z-10">
              <div>
                <span className={`text-[8px] font-black uppercase tracking-widest px-2 py-1 rounded-full ${mode === 'eco' ? 'bg-[#10B981]/10 text-[#10B981]' : 'bg-[#3B82F6]/10 text-[#3B82F6]'}`}>
                  {mode === 'eco' ? 'Ekolojik Rapor' : 'Güvenlik Raporu'}
                </span>
                <h2 className="text-2xl font-black mt-2 text-gray-900 leading-normal">{result.brand_name}</h2>
              </div>
              <div className="text-right">
                <div className={`text-4xl font-black ${result.puan > 80 ? 'text-[#10B981]' : 'text-orange-500'}`}>
                  {result.puan}<span className="text-base text-gray-400">/100</span>
                </div>
              </div>
            </div>

            <div className="flex items-start gap-3 p-5 bg-gray-50/50 rounded-2xl border border-gray-100 mb-6">
              <div className={`p-2 rounded-lg bg-white shadow-sm ${mode === 'eco' ? 'text-[#10B981]' : 'text-[#3B82F6]'}`}>
                {result.puan > 80 ? <CheckCircle2 size={20} /> : <AlertCircle size={20} />}
              </div>
              <div className="flex-1">
                <h4 className="font-bold text-gray-400 mb-1 text-[10px] uppercase tracking-wide opacity-60">Yapay Zeka Özeti</h4>
                <p className="text-gray-700 font-medium text-sm leading-relaxed">{result.tespit}</p>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="p-3 bg-gray-50/50 rounded-xl border border-gray-100 text-center transition-colors hover:bg-white">
                <p className="text-[8px] font-bold text-gray-400 uppercase mb-1 tracking-widest">Risk</p>
                <p className="text-sm font-black text-gray-900">{result.risk}</p>
              </div>
              <div className="p-3 bg-gray-50/50 rounded-xl border border-gray-100 text-center transition-colors hover:bg-white">
                <p className="text-[8px] font-bold text-gray-400 uppercase mb-1 tracking-widest">Kalan Hak</p>
                <p className="text-sm font-black text-gray-900">{userStats.remaining}</p>
              </div>
            </div>
          </div>

          {/* Aksiyonlar */}
          <div className="flex justify-between mt-8 px-4">
            <button 
              onClick={downloadPDF} 
              className="flex items-center gap-2 text-[10px] font-bold text-gray-400 hover:text-black transition-all group"
            >
              <Download size={16} className="group-hover:translate-y-0.5 transition-transform" /> 
              PDF Arşivle
            </button>
            
            {userStats.remaining === 0 && (
              <button 
                onClick={() => window.location.href='/pricing'} 
                className="flex items-center gap-2 text-[10px] font-bold text-[#3B82F6] hover:underline animate-bounce"
              >
                <Lock size={14} /> Limit Doldu! Pro'ya Geç
              </button>
            )}
          </div>
        </div>
      )}

      {/* FOOTER: Daha kibar ve okunaklı */}
      <footer className="mt-auto py-10 text-center w-full max-w-2xl border-t border-gray-100/50 opacity-50">
        <p className="text-[9px] text-gray-400 font-bold uppercase tracking-[0.2em] mb-2">
          © 2026 GreenScan AI • Sürdürülebilirlik Ağı
        </p>
        <p className="text-[8px] text-gray-400 leading-normal font-medium italic px-8">
          * Yapay zeka tarafından üretilen sonuçlar birer **öneridir**; kesinlik taahhüt etmez.
        </p>
      </footer>
    </div>
  );
};

export default AnalysisPage;
