import React, { useState } from 'react';
import { Mail, Lock, LogIn, UserPlus } from 'lucide-react';
import { useNavigate } from 'react-router-dom'; // Yönlendirme için eklendi
import axios from 'axios';

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Yönlendirme kancası

  const handleAuth = async (e) => {
    e.preventDefault();
    
    // Basit bir kontrol
    if (!email || !password) {
      alert("Lütfen tüm alanları doldurun!");
      return;
    }

    try {
      // Backend'e giriş isteği gönderiyoruz
      const response = await axios.post('http://localhost:8000/api/login', { 
        email: email 
      });

      if (response.data.status === "success") {
        // 1. Backend'den gelen user_id'yi tarayıcı hafızasına alıyoruz
        localStorage.setItem('user_id', response.data.user_id);
        localStorage.setItem('user_role', response.data.role);
        
        // 2. Kullanıcıya bilgi verip ana sayfaya uçuruyoruz
        alert(`Hoş geldin! Üyeliğin aktif edildi. Kalan Hakkın: ${response.data.remaining}`);
        navigate('/'); // Seni ana sayfaya atar
      } else {
        alert("Giriş başarısız: " + response.data.message);
      }
    } catch (error) {
      console.error("Giriş hatası:", error);
      alert("Sunucuya bağlanılamadı!");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#F9FAFB] p-6">
      <div className="w-full max-w-md bg-white rounded-[3rem] shadow-2xl p-10 border border-gray-100">
        <div className="text-center mb-10">
          <h2 className="text-3xl font-black text-gray-900 mb-2">
            {isLogin ? 'Tekrar Hoş Geldin' : 'Aramıza Katıl'}
          </h2>
          <p className="text-gray-500 font-medium">
            {isLogin ? 'Analizlerine kaldığın yerden devam et.' : 'Üye ol, ücretsiz analiz hakkını 7\'ye çıkar!'}
          </p>
        </div>

        <form className="space-y-4" onSubmit={handleAuth}>
          <div className="relative">
            <Mail className="absolute left-4 top-4 text-gray-400" size={20} />
            <input 
              type="email" 
              required
              placeholder="E-posta Adresi" 
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full h-14 pl-12 pr-6 rounded-2xl bg-gray-50 outline-none border-2 border-transparent focus:border-[#10B981]/30 transition-all" 
            />
          </div>
          <div className="relative">
            <Lock className="absolute left-4 top-4 text-gray-400" size={20} />
            <input 
              type="password" 
              required
              placeholder="Şifre" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full h-14 pl-12 pr-6 rounded-2xl bg-gray-50 outline-none border-2 border-transparent focus:border-[#10B981]/30 transition-all" 
            />
          </div>
          
          <button type="submit" className="w-full h-14 bg-black text-white rounded-2xl font-bold flex items-center justify-center gap-2 hover:scale-[1.02] active:scale-95 transition-all mt-6">
            {isLogin ? <LogIn size={20} /> : <UserPlus size={20} />}
            {isLogin ? 'Giriş Yap' : 'Kayıt Ol'}
          </button>
        </form>

        <div className="mt-8 text-center">
          <button onClick={() => setIsLogin(!isLogin)} className="text-sm font-bold text-gray-400 hover:text-[#10B981] transition-all">
            {isLogin ? 'Hesabın yok mu? Kayıt Ol' : 'Zaten üye misin? Giriş Yap'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
