import React, { useState } from 'react'; // useState eklendi
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import Sidebar from './components/Sidebar';
import AnalysisPage from './pages/AnalysisPage';
import './index.css';

function App() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // Menü durumu

  return (
    <AuthProvider>
      <Router>
        <div className="flex bg-[#F9FAFB] min-h-screen transition-all duration-300">
          {/* Menü durumunu Sidebar'a gönderiyoruz */}
          <Sidebar isOpen={isSidebarOpen} setIsOpen={setIsSidebarOpen} />
          
          {/* İçerik alanı menü kapalıyken sola yaslanacak */}
          <main className={`flex-1 transition-all duration-300 ${isSidebarOpen ? 'ml-[280px]' : 'ml-20'}`}>
            <Routes>
              <Route path="/" element={<AnalysisPage />} />
            </Routes>
          </main>
        </div>
      </Router>
    </AuthProvider>
  );
}
export default App;
