import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { LogIn, CreditCard, User } from 'lucide-react';
import axios from 'axios';

const Navbar = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem('user_id');
  const userName = localStorage.getItem('user_name') || 'Kullanıcı';
  const [status, setStatus] = useState({ remaining: 3 });

  useEffect(() => {
    if (userId) {
      const fetchStatus = async () => {
        try {
          const res = await axios.get(`http://localhost:8000/api/user-status/${userId}`);
          setStatus({ remaining: res.data.remaining });
        } catch (e) { console.error(e); }
      };
      fetchStatus();
    }
  }, [userId]);

  return (
    <div className="fixed top-0 right-0 left-0 h-20 z-40 flex items-center justify-end px-12 pointer-events-none">
      <div className="flex items-center gap-3 pointer-events-auto bg-white/90 backdrop-blur-md p-2 rounded-2xl border border-white/20 shadow-xl">
        {!userId ? (
          <div className="flex items-center gap-2">
            <button onClick={() => navigate('/pricing')} className="px-5 py-2.5 font-bold text-gray-600 hover:bg-gray-50 rounded-xl transition-all">Paketler</button>
            <button onClick={() => navigate('/auth')} className="px-6 py-2.5 bg-black text-white rounded-xl font-bold hover:scale-105 transition-all">Giriş Yap</button>
          </div>
        ) : (
          <div className="flex items-center gap-4 px-4 py-1">
            <div className="flex flex-col items-end">
              <span className="text-sm font-black text-gray-900 font-manrope">{userName}</span>
              <span className="text-[10px] font-bold text-[#10B981] uppercase tracking-tighter">
                Kalan Hak: {status.remaining}
              </span>
            </div>
            <div 
              onClick={() => navigate('/profile')}
              className="w-10 h-10 rounded-xl bg-gray-100 flex items-center justify-center text-gray-600 cursor-pointer hover:bg-black hover:text-white transition-all shadow-inner"
            >
              <User size={20} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Navbar;
