import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import Sidebar from './components/Sidebar';
import AnalysisPage from './pages/AnalysisPage';
import HistoryPage from './pages/HistoryPage'; // Geçmiş sayfasını ekledik
import './index.css';

function App() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  return (
    <AuthProvider>
      <Router>
        <div className="flex bg-[#F9FAFB] min-h-screen transition-all duration-300">
          {/* Menü durumunu Sidebar'a gönderiyoruz */}
          <Sidebar isOpen={isSidebarOpen} setIsOpen={setIsSidebarOpen} />
          
          {/* İçerik alanı: Sidebar kapalıyken ml-20, açıkken ml-[280px] */}
          <main className={`flex-1 transition-all duration-300 ${isSidebarOpen ? 'ml-[280px]' : 'ml-20'}`}>
            <Routes>
              {/* Ana Sayfa: Analiz Ekranı */}
              <Route path="/" element={<AnalysisPage />} />
              
              {/* Geçmiş Sayfası: Yapılan Analizlerin Listesi */}
              <Route path="/history" element={<HistoryPage />} />
              
              {/* İleride profil vb. sayfalar gelirse buraya eklenebilir */}
            </Routes>
          </main>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
