import React, { useState } from 'react';
import { Mail, Lock, ArrowRight, UserPlus, LogIn } from 'lucide-react';

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#F9FAFB] p-6">
      <div className="w-full max-w-md bg-white rounded-[3rem] shadow-2xl p-10 border border-gray-100">
        <div className="text-center mb-10">
          <h2 className="text-3xl font-black text-gray-900 mb-2">
            {isLogin ? 'Tekrar Hoş Geldin' : 'Aramıza Katıl'}
          </h2>
          <p className="text-gray-500 font-medium">
            {isLogin ? 'Analizlerine kaldığın yerden devam et.' : 'Üye ol, ücretsiz analiz hakkını 7\'ye çıkar!'}
          </p>
        </div>

        <form className="space-y-4">
          <div className="relative">
            <Mail className="absolute left-4 top-4 text-gray-400" size={20} />
            <input type="email" placeholder="E-posta Adresi" className="w-full h-14 pl-12 pr-6 rounded-2xl bg-gray-50 outline-none border-2 border-transparent focus:border-[#10B981]/30 transition-all" />
          </div>
          <div className="relative">
            <Lock className="absolute left-4 top-4 text-gray-400" size={20} />
            <input type="password" placeholder="Şifre" className="w-full h-14 pl-12 pr-6 rounded-2xl bg-gray-50 outline-none border-2 border-transparent focus:border-[#10B981]/30 transition-all" />
          </div>
          
          <button className="w-full h-14 bg-black text-white rounded-2xl font-bold flex items-center justify-center gap-2 hover:scale-[1.02] active:scale-95 transition-all mt-6">
            {isLogin ? <LogIn size={20} /> : <UserPlus size={20} />}
            {isLogin ? 'Giriş Yap' : 'Kayıt Ol'}
          </button>
        </form>

        <div className="mt-8 text-center">
          <button onClick={() => setIsLogin(!isLogin)} className="text-sm font-bold text-gray-400 hover:text-[#10B981] transition-all">
            {isLogin ? 'Hesabın yok mu? Kayıt Ol' : 'Zaten üye misin? Giriş Yap'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
