import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import Sidebar from './components/Sidebar';
import AnalysisPage from './pages/AnalysisPage';
import HistoryPage from './pages/HistoryPage';
import ProfilePage from './pages/ProfilePage';
import AuthPage from './pages/AuthPage'; // Yeni: Giriş/Kayıt Sayfası
import PricingPage from './pages/PricingPage'; // Yeni: Paketler Sayfası
import './index.css';

function App() {
  // Sidebar'ın açık/kapalı durumunu yönetiyoruz
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  return (
    <AuthProvider>
      <Router>
        <div className="flex bg-[#F9FAFB] min-h-screen transition-all duration-300">
          
          {/* Sol Menü: Açılış-Kapanış durumunu yönetiyor */}
          <Sidebar isOpen={isSidebarOpen} setIsOpen={setIsSidebarOpen} />
          
          {/* Ana İçerik Alanı: Sidebar durumuna göre genişleyip daralıyor */}
          <main className={`flex-1 transition-all duration-300 ${isSidebarOpen ? 'ml-[280px]' : 'ml-20'}`}>
            <Routes>
              {/* 1. Sayfa: Ana Analiz ve PDF İndirme Ekranı */}
              <Route path="/" element={<AnalysisPage />} />
              
              {/* 2. Sayfa: Yapılan Analizlerin Listesi */}
              <Route path="/history" element={<HistoryPage />} />
              
              {/* 3. Sayfa: Kullanıcı İstatistikleri ve Başarı Rozetleri */}
              <Route path="/profile" element={<ProfilePage />} />

              {/* 4. Sayfa: Üye Ol ve Giriş Yap Ekranı */}
              <Route path="/auth" element={<AuthPage />} />

              {/* 5. Sayfa: Limit Artırma ve Paket Satın Alma Ekranı */}
              <Route path="/pricing" element={<PricingPage />} />
              
            </Routes>
          </main>
          
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
