import React, { useState, useEffect } from 'react';
import { Search, Sparkles, Leaf, ShieldCheck, AlertCircle, CheckCircle2, Download, Lock } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

const AnalysisPage = () => {
  const { token, loginAsGuest } = useAuth();
  const [brand, setBrand] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [mode, setMode] = useState('eco');
  const [userStats, setUserStats] = useState({ remaining: 3, role: 'guest' });

  // Dinamik karşılama için kullanıcı ismini ve giriş durumunu alıyoruz
  const isLoggedIn = localStorage.getItem('user_id');
  const firstName = localStorage.getItem('user_name') || '';

  // Sayfa açıldığında ve her analizden sonra güncel limit bilgisini çekiyoruz
  useEffect(() => {
    const fetchStatus = async () => {
      const userId = localStorage.getItem('user_id') || 'guest_user';
      try {
        const res = await axios.get(`http://localhost:8000/api/user-status/${userId}`);
        setUserStats({ remaining: res.data.remaining, role: res.data.role });
      } catch (e) { 
        console.error("Statü bilgisi güncellenemedi", e); 
      }
    };
    fetchStatus();
  }, [result]); 

  // PDF Oluşturma ve İndirme Fonksiyonu
  const downloadPDF = async () => {
    const element = document.getElementById('report-card');
    const canvas = await html2canvas(element, { scale: 2 });
    const imgData = canvas.toDataURL('image/png');
    const pdf = new jsPDF('p', 'mm', 'a4');
    const imgProps = pdf.getImageProperties(imgData);
    const pdfWidth = pdf.internal.pageSize.getWidth();
    const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
    
    pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
    pdf.save(`GreenScan_Analiz_${result.brand_name}.pdf`);
  };

  const handleSearch = async () => {
    if (!brand.trim()) return;
    setLoading(true);
    setResult(null);
    
    const currentUserId = localStorage.getItem('user_id') || 'guest_user';

    try {
      if (!token) await loginAsGuest();
      const response = await axios.post('http://localhost:8000/api/analyze', { 
        brand_name: brand,
        mode: mode,
        user_id: currentUserId 
      });

      if (response.data.error) {
        alert(response.data.error + "\n" + response.data.message);
        return;
      }

      setResult(response.data);
    } catch (e) { 
      console.error(e); 
      alert("Analiz sırasında bir hata oluştu. Sunucuyu kontrol edin.");
    } finally { 
      setLoading(false); 
    }
  };

  return (
    <div className="p-12 lg:p-20 flex flex-col items-center min-h-screen bg-[#F9FAFB]">
      
      {/* BAŞLIK BÖLÜMÜ: Giriş yapan kullanıcıya özel isimli karşılama */}
      <div className="text-center mb-12 mt-8 animate-in fade-in slide-in-from-top-4 duration-700">
        <h1 className="text-5xl lg:text-7xl font-black mb-6 bg-gradient-to-r from-[#10B981] to-[#3B82F6] bg-clip-text text-transparent font-manrope tracking-tight leading-tight">
          {isLoggedIn 
            ? `Merhaba ${firstName}, bugün hangi markayı analiz edelim?` 
            : "Akıllı Marka Analizi"}
        </h1>
        <p className="text-gray-400 text-lg font-medium max-w-2xl mx-auto italic">
          Yapay zeka ile sürdürülebilirlik ve güvenlik denetimi parmaklarınızın ucunda.
        </p>
      </div>

      {/* MOD SEÇİCİ: Doğa Dostu veya Güvenli Alışveriş */}
      <div className="flex gap-3 mb-10 p-1.5 bg-gray-200/50 rounded-2xl shadow-inner">
        <button 
          onClick={() => setMode('eco')} 
          className={`flex items-center gap-2 px-8 py-4 rounded-xl font-bold transition-all duration-300 ${
            mode === 'eco' ? 'bg-white text-[#10B981] shadow-lg scale-105' : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          <Leaf size={20} /> Doğa Dostu
        </button>
        <button 
          onClick={() => setMode('safety')} 
          className={`flex items-center gap-2 px-8 py-4 rounded-xl font-bold transition-all duration-300 ${
            mode === 'safety' ? 'bg-white text-[#3B82F6] shadow-lg scale-105' : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          <ShieldCheck size={20} /> Güvenli Alışveriş
        </button>
      </div>

      {/* ARAMA ÇUBUĞU */}
      <div className="relative w-full max-w-2xl group shadow-[0_20px_50px_rgba(16,185,129,0.1)] rounded-full">
        <input 
          className="w-full h-24 px-12 rounded-full bg-white outline-none text-2xl border-2 border-transparent focus:border-[#10B981]/20 transition-all shadow-sm placeholder:text-gray-300" 
          placeholder={mode === 'eco' ? "Hangi markanın doğasını inceleyelim?" : "Hangi mağazanın güvenliğini sorgulayalım?"}
          value={brand} 
          onChange={(e) => setBrand(e.target.value)} 
          onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
        />
        <button 
          onClick={handleSearch} 
          disabled={loading} 
          className={`absolute right-4 top-4 h-16 px-10 rounded-full flex items-center gap-2 font-black transition-all ${
            loading ? 'bg-gray-400 animate-pulse' : 'bg-black text-white hover:bg-gray-900 active:scale-95'
          }`}
        >
          {loading ? <Sparkles className="animate-spin" /> : <Search size={24} />}
          <span>{loading ? 'Analiz Ediliyor...' : 'Analiz Et'}</span>
        </button>
      </div>

      {/* SONUÇ KARTI */}
      {result && (
        <div className="mt-16 w-full max-w-2xl animate-in fade-in slide-in-from-bottom-8 duration-500">
          <div id="report-card" className="p-12 border border-gray-100 rounded-[4rem] bg-white shadow-[0_50px_100px_-20px_rgba(0,0,0,0.1)] relative overflow-hidden">
            <div className={`absolute top-0 right-0 w-48 h-48 opacity-5 -mr-12 -mt-12 rounded-full ${mode === 'eco' ? 'bg-[#10B981]' : 'bg-[#3B82F6]'}`} />
            
            <div className="flex justify-between items-start mb-10 relative z-10">
              <div>
                <span className={`text-[10px] font-black uppercase tracking-[0.2em] px-4 py-1.5 rounded-full ${mode === 'eco' ? 'bg-[#10B981]/10 text-[#10B981]' : 'bg-[#3B82F6]/10 text-[#3B82F6]'}`}>
                  {mode === 'eco' ? 'Ekolojik Rapor' : 'Güvenlik Raporu'}
                </span>
                <h2 className="text-5xl font-black mt-4 text-gray-900 tracking-tight">{result.brand_name}</h2>
              </div>
              <div className="text-right">
                <div className={`text-6xl font-black ${result.puan > 80 ? 'text-[#10B981]' : 'text-orange-500'}`}>
                  {result.puan}<span className="text-xl text-gray-400">/100</span>
                </div>
                <p className="text-[10px] font-black text-gray-400 mt-2 uppercase tracking-widest">Global Skor</p>
              </div>
            </div>

            <div className="flex items-start gap-5 p-8 bg-gray-50/50 rounded-[2.5rem] border border-gray-100 mb-8 backdrop-blur-sm">
              <div className={`p-4 rounded-2xl shadow-sm ${mode === 'eco' ? 'bg-white text-[#10B981]' : 'bg-white text-[#3B82F6]'}`}>
                {result.puan > 80 ? <CheckCircle2 size={32} /> : <AlertCircle size={32} />}
              </div>
              <div>
                <h4 className="font-black text-gray-900 mb-2 uppercase text-xs tracking-wider opacity-60">Yapay Zeka Tespiti</h4>
                <p className="text-gray-700 leading-relaxed font-semibold text-lg">{result.tespit}</p>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-6">
              <div className="p-6 bg-gray-50/50 rounded-3xl text-center border border-gray-100">
                <p className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2">Risk Değerlendirmesi</p>
                <p className="text-xl font-black text-gray-900">{result.risk}</p>
              </div>
              <div className="p-6 bg-gray-50/50 rounded-3xl text-center border border-gray-100">
                <p className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2">Kalan Analiz Hakkı</p>
                <p className="text-xl font-black text-gray-900">{userStats.remaining}</p>
              </div>
            </div>
          </div>

          {/* Aksiyon Butonları */}
          <div className="flex justify-between mt-10 px-8">
            <button 
              onClick={downloadPDF} 
              className="flex items-center gap-3 text-sm font-black text-gray-400 hover:text-black transition-all group"
            >
              <Download size={22} className="group-hover:translate-y-0.5 transition-transform" /> 
              Raporu PDF Olarak Arşivle
            </button>
            
            {userStats.remaining === 0 && (
              <button 
                onClick={() => window.location.href='/pricing'} 
                className="flex items-center gap-2 text-sm font-black text-[#3B82F6] hover:underline animate-bounce"
              >
                <Lock size={20} /> Limit Doldu! Pro'ya Geç
              </button>
            )}
          </div>
        </div>
      )}

      {/* FOOTER: Sorumluluk Reddi ve Telif Bilgisi */}
      <footer className="mt-auto py-16 text-center w-full max-w-3xl border-t border-gray-100/50 opacity-60">
        <p className="text-[11px] text-gray-400 font-bold uppercase tracking-[0.3em] mb-4">
          © 2026 GreenScan AI • Global Sürdürülebilirlik Ağı
        </p>
        <p className="text-[10px] text-gray-400 leading-relaxed font-medium italic">
          * Sunulan analiz sonuçları yapay zeka tarafından halka açık veriler ışığında üretilmiş birer **öneridir**; 
          kesinlik taahhüt etmez ve hiçbir şekilde yatırım veya profesyonel alışveriş tavsiyesi niteliği taşımaz.
        </p>
      </footer>
    </div>
  );
};

export default AnalysisPage;
