import React, { useState } from 'react';
import { Search, Sparkles } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const AnalysisPage = () => {
  const { token, loginAsGuest } = useAuth();
  const [brand, setBrand] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);

  const handleSearch = async () => {
    if (!brand.trim()) return;
    setLoading(true);
    try {
      if (!token) await loginAsGuest();
      const response = await axios.post('http://localhost:8000/api/analyze', { brand_name: brand });
      setResult(response.data);
    } catch (e) { console.error(e); } finally { setLoading(false); }
  };

  return (
    <div className="p-20 flex flex-col items-center">
      <h1 className="text-5xl font-black mb-12 bg-gradient-to-r from-[#10B981] to-[#3B82F6] bg-clip-text text-transparent font-manrope">
        Hangi markayı inceleyelim?
      </h1>
      <div className="relative w-full max-w-2xl group">
        <input 
          className="w-full h-16 px-8 rounded-full bg-gray-100 outline-none text-xl focus:ring-4 focus:ring-[#10B981]/20 transition-all" 
          placeholder="Marka adı yazın... (örn: Apple)" 
          value={brand} 
          onChange={(e) => setBrand(e.target.value)} 
        />
        <button onClick={handleSearch} className="absolute right-2 top-2 h-12 w-12 bg-black text-white rounded-full flex items-center justify-center hover:scale-105 active:scale-95 transition-all">
          {loading ? <Sparkles className="animate-spin" /> : <Search />}
        </button>
      </div>
      {result && (
        <div className="mt-12 p-8 border-2 border-gray-100 rounded-[2.5rem] bg-white shadow-2xl w-full max-w-2xl">
          <h2 className="text-3xl font-bold mb-4">{result.brand_name} - Puan: {result.puan}</h2>
          <p className="text-gray-600 text-lg leading-relaxed">{result.tespit}</p>
        </div>
      )}
    </div>
  );
};
export default AnalysisPage;
